import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CalendarComponent from './components/Calendar.jsx';
import TimeSlots from './components/TimeSlots.jsx';
import BookingForm from './components/BookingForm';
import Confirmation from './components/Confirmation';

function App() {
  const [date, setDate] = useState(null);
  const [timeFrom, setTimeFrom] = useState('');
  const [timeTo, setTimeTo] = useState('');
  const [bookingDetails, setBookingDetails] = useState(null);

  const handleDateSelect = (selectedDate) => {
    setDate(selectedDate);
  };

  const handleTimeSelect = (from, to) => {
    setTimeFrom(from);
    setTimeTo(to);
  };

  const handleBookingSubmit = (details) => {
    setBookingDetails({
      ...details,
      date,
      timeFrom,
      timeTo,
    });
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<CalendarComponent onDateSelect={handleDateSelect} />} />
        <Route path="/time" element={<TimeSlots onTimeSelect={handleTimeSelect} />} />
        <Route path="/form" element={<BookingForm onBookingSubmit={handleBookingSubmit} />} />
        <Route path="/confirmation" element={<Confirmation bookingDetails={bookingDetails} />} />
      </Routes>
    </Router>
  );
}

export default App;
