import React from 'react';
import '../Confirmation.css';

function BookingConfirmed({ bookingDetails, onConfirmBooking }) {
  const {
    name,
    email,
    phone,
    address,
    postalCode,
    bookingDate,
    timeFrom,
    timeTo,
    serviceName,
    servicePrice,
    totalArea,
    discount,
    amount,
  } = bookingDetails || {};

  const formattedDate = bookingDate
    ? new Date(bookingDate).toLocaleDateString('en-GB', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      })
    : 'No Date';

  return (
    <div className="booking-confirmed-container">
      <h2>Review Your Booking</h2>
      {/* ... display booking details ... */}
      <button onClick={() => onConfirmBooking(bookingDetails)}>
        Confirm Booking
      </button>
    </div>
  );
}

export default BookingConfirmed;
