import React, { useState } from 'react';
import { Provider } from 'react-redux';
import CalendarComponent from './components/Calendar';
import TimeSlots from './components/TimeSlots';
import BookingForm from './components/BookingForm';
import ServiceDetails from './components/ServiceDetails';
import Confirmation from './components/Confirmation';
import store from './store'; // Import the Redux store
import './styles.css';

function App() {
  return (
    <Provider store={store}>
      <BookingApp />
    </Provider>
  );
}

function BookingApp() {
  const [stage, setStage] = useState(1);

  // Function to handle progressing to the service stage
  const handleProceedToService = (details) => {
    // You can add more logic here as needed
    setStage(4);  // Proceed to the service details stage
  };

  return (
    <div>
      {stage === 1 && <CalendarComponent setStage={setStage} />}
      {stage === 2 && <TimeSlots setStage={setStage} />}
      {stage === 3 && <BookingForm onProceedToService={handleProceedToService} />} {/* Pass the function here */}
      {stage === 4 && <ServiceDetails setStage={setStage} />}
      {stage === 5 && <Confirmation />}
    </div>
  );
}

export default App;
