import React, { useState } from 'react';
import CalendarComponent from './components/Calendar';
import TimeSlots from './components/TimeSlots';
import BookingForm from './components/BookingForm';
import ServiceDetails from './components/ServiceDetails';  // New ServiceDetails component
import Confirmation from './components/Confirmation';
import './styles.css';  // Import the global styles

function App() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeFrom, setSelectedTimeFrom] = useState('');
  const [selectedTimeTo, setSelectedTimeTo] = useState('');
  const [userDetails, setUserDetails] = useState(null);  // Store user details
  const [serviceDetails, setServiceDetails] = useState(null);  // Store service details
  const [bookingDetails, setBookingDetails] = useState(null);  // Store final booking details
  const [stage, setStage] = useState(1);  // Control flow through stages

  // Handle Date Selection
  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setStage(2);  // Move to the next stage (Time Slot selection)
  };

  // Handle Time Slot Selection
  const handleTimeSelect = (from, to) => {
    setSelectedTimeFrom(from);
    setSelectedTimeTo(to);
    setStage(3);  // Move to the next stage (User Details Form)
  };

  // Handle User Details Submission and Move to Service Details
  const handleProceedToService = (details) => {
    setUserDetails(details);  // Store user details
    setStage(4);  // Move to the next stage (Service Details Form)
  };

  // Handle Service Details Submission and Confirm Booking
  const handleServiceDetailsSubmit = (details) => {
    setServiceDetails(details);  // Store service details

    // Combine the details into final booking details and move to the confirmation stage
    setBookingDetails({
      ...userDetails,  // Include user details
      ...serviceDetails,  // Include service details
      date: selectedDate,
      timeFrom: selectedTimeFrom,
      timeTo: selectedTimeTo,
    });

    setStage(5);  // Move to the final confirmation stage
  };

  return (
    <div>
      {stage === 1 && <CalendarComponent onDateSelect={handleDateSelect} />}
      {stage === 2 && <TimeSlots onTimeSelect={handleTimeSelect} />}
      {stage === 3 && <BookingForm onProceedToService={handleProceedToService} />}
      {stage === 4 && <ServiceDetails onServiceDetailsSubmit={handleServiceDetailsSubmit} />}
      {stage === 5 && <Confirmation bookingDetails={bookingDetails} />}  {/* Move to final confirmation */}
    </div>
  );
}

export default App;
