import React, { useState } from 'react';
import CalendarComponent from './components/Calendar';
import TimeSlots from './components/TimeSlots';
import BookingForm from './components/BookingForm';
import ServiceDetails from './components/ServiceDetails';
import BookingConfirmed from './components/Confirmation';
import ProgressBar from './components/ProgressBar';
import './styles.css';

function App() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeFrom, setSelectedTimeFrom] = useState('');
  const [selectedTimeTo, setSelectedTimeTo] = useState('');
  const [userDetails, setUserDetails] = useState({});
  const [serviceDetails, setServiceDetails] = useState({});
  const [stage, setStage] = useState(1); // Start with ServiceDetails
  const [discountMessage, setDiscountMessage] = useState('');
  const [discount, setDiscount] = useState(0);

  // Step 1: Handle service details confirmation
  const handleConfirmService = (serviceDetails) => {
    setServiceDetails(serviceDetails);
    setStage(2); // Move to Calendar and TimeSlots
  };

  // Step 2: Handle date selection from Calendar
  const handleDateSelect = (date) => {
    setSelectedDate(date);
  };

  // Step 3: Handle time slot selection
  const handleTimeSelect = (from, to) => {
    setSelectedTimeFrom(from);
    setSelectedTimeTo(to);
  };

  // Step 4: Proceed to BookingForm only when both date and time are selected
  const handleProceedToBookingForm = () => {
    if (selectedDate && selectedTimeFrom && selectedTimeTo) {
      setStage(3); // Move to BookingForm
    } else {
      alert('Please select both a date and a time slot.');
    }
  };

  // Step 5: Handle user details submission
  const handleProceedToService = (details) => {
    setUserDetails(details);

    // Combine all data and move to confirmation directly
    const bookingDetails = {
      ...details,
      ...serviceDetails,
      bookingDate: selectedDate, // Include the selected date
      timeFrom: selectedTimeFrom, // Include time slot
      timeTo: selectedTimeTo,
      discount,
      amount: serviceDetails.amount - (serviceDetails.amount * discount) / 100, // Adjust amount with discount
    };
    setServiceDetails(bookingDetails);
    setStage(4); // Move to BookingConfirmed
  };

  return (
    <div>
      {/* Progress Bar at the top */}
      <ProgressBar currentStage={stage} />

      {/* Render components based on the stage */}
      {stage === 1 && <ServiceDetails discount={discount} onConfirmBooking={handleConfirmService} />}

      {stage === 2 && (
        <div>
          <CalendarComponent onDateSelect={handleDateSelect} />
          <TimeSlots onTimeSelect={handleTimeSelect} />
          <button onClick={handleProceedToBookingForm} className="confirm-button">Proceed</button>
        </div>
      )}

      {stage === 3 && (
        <>
          {discountMessage && <p>{discountMessage}</p>}
          <BookingForm onProceedToService={handleProceedToService} />
        </>
      )}

      {stage === 4 && <BookingConfirmed bookingDetails={serviceDetails} />}
    </div>
  );
}

export default App;
