import React, { useState } from 'react';
import CalendarComponent from './components/Calendar';
import TimeSlots from './components/TimeSlots';
import BookingForm from './components/BookingForm';
import Confirmation from './components/Confirmation';
import './styles.css';  // Import the global styles

function App() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeFrom, setSelectedTimeFrom] = useState('');
  const [selectedTimeTo, setSelectedTimeTo] = useState('');
  const [bookingDetails, setBookingDetails] = useState(null);
  const [stage, setStage] = useState(1);

  // Handle Date Selection
  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setStage(2);  // Move to the next stage
  };

  // Handle Time Slot Selection
  const handleTimeSelect = (from, to) => {
    setSelectedTimeFrom(from);
    setSelectedTimeTo(to);
    setStage(3);  // Move to the next stage
  };

  // Handle User Details Submission
  const handleBookingSubmit = (details) => {
    setBookingDetails({
      ...details,
      date: selectedDate,
      timeFrom: selectedTimeFrom,
      timeTo: selectedTimeTo,
    });
    setStage(4);  // Move to the next stage
  };

  // Send Email Confirmation (can be linked to backend)
  const sendEmailConfirmation = () => {
    if (bookingDetails) {
      console.log('Sending email to:', bookingDetails.email);
      // Simulated email sending; implement using API service
    }
  };

  // Render the correct component based on the stage
  return (
    <div>
      {stage === 1 && <CalendarComponent onDateSelect={handleDateSelect} />}
      {stage === 2 && <TimeSlots onTimeSelect={handleTimeSelect} />}
      {stage === 3 && <BookingForm onBookingSubmit={handleBookingSubmit} />}
      {stage === 4 && (
        <Confirmation bookingDetails={bookingDetails} />
        // Call the email sending function when booking is confirmed
        //sendEmailConfirmation()
      )}
    </div>
  );
}

export default App;
