import React, { useState } from 'react';
import ProgressBar from './components/ProgressBar';
import ServiceDetails from './components/ServiceDetails';
import CalendarComponent from './components/Calendar';
import TimeSlots from './components/TimeSlots';
import BookingForm from './components/BookingForm';
import BookingConfirmed from './components/Confirmation';
import './styles.css';

function App() {
  const [stage, setStage] = useState(1);
  const [serviceDetails, setServiceDetails] = useState({});
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeFrom, setSelectedTimeFrom] = useState('');
  const [selectedTimeTo, setSelectedTimeTo] = useState('');
  const [userDetails, setUserDetails] = useState({});
  const [discountMessage, setDiscountMessage] = useState('');
  const [discount, setDiscount] = useState(0);

  // Step 1: Handle service confirmation
  const handleConfirmService = (details) => {
    setServiceDetails(details);
    setStage(2);
  };

  // Step 2: Handle date selection
  const handleDateSelect = (date) => {
    setSelectedDate(date);
  };

  // Step 3: Handle time slot selection
  const handleTimeSelect = (from, to) => {
    setSelectedTimeFrom(from);
    setSelectedTimeTo(to);
  };

  // Step 4: Move to user info form after date and time selection
  const handleProceedToUserInfo = () => {
    if (selectedDate && selectedTimeFrom && selectedTimeTo) {
      setStage(3);
    } else {
      alert('Please select both a date and a time slot.');
    }
  };

  // Step 5: Handle user details submission and proceed to confirmation
  const handleProceedToService = (details) => {
    setUserDetails(details);
    setStage(4);
  };

  // Step 6: Function to check if user exists for discount
  const checkUserExists = async (email) => {
    const query = `
      query {
        checkUserExists(email: "${email}")
      }
    `;
    try {
      const response = await fetch('http://localhost:4000/graphql', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query }),
      });
      const result = await response.json();
      return result.data?.checkUserExists ?? false;
    } catch (error) {
      console.error('Error checking user:', error);
      return false;
    }
  };

  // Step 7: Save booking data to backend (this also triggers email sending)
  const saveBookingToBackend = async (bookingDetails) => {
    const query = `
      mutation {
        addBooking(
          name: "${bookingDetails.name}",
          email: "${bookingDetails.email}",
          phone: "${bookingDetails.phone}",
          address: "${bookingDetails.address}",
          postalCode: "${bookingDetails.postalCode}",
          bookingDate: "${bookingDetails.bookingDate}",
          bookingStart: "${bookingDetails.timeFrom}",
          bookingEnd: "${bookingDetails.timeTo}",
          serviceName: "${bookingDetails.serviceName}",
          servicePrice: ${bookingDetails.servicePrice},
          totalArea: ${bookingDetails.totalArea},
          discount: ${bookingDetails.discount},
          amount: ${bookingDetails.amount}
        ) {
          id
          name
        }
      }
    `;
    try {
      const response = await fetch('http://localhost:4000/graphql', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query }),
      });

      if (!response.ok) throw new Error('Failed to save booking');

      const result = await response.json();
      if (result.errors) {
        console.error('GraphQL Errors:', result.errors);
        return null;
      }

      return result.data;
    } catch (error) {
      console.error('Error saving booking:', error);  // Log the full error
      alert('Error saving your booking. Please try again.');
      return null;
    }
  };


  // Step 8: Final booking confirmation logic, with discount check and email sending
  const handleConfirmBooking = async () => {
    const userExists = await checkUserExists(userDetails.email);
    let appliedDiscount = 0;

    if (userExists) {
      setDiscountMessage('Congratulations! You are already in our system and get a 10% discount!');
      appliedDiscount = 10;
    }

    const discountedAmount = serviceDetails.amount - (serviceDetails.amount * (appliedDiscount / 100));

    const bookingDetails = {
      ...userDetails,
      ...serviceDetails,
      bookingDate: selectedDate ? selectedDate.toISOString() : 'No Date',
      timeFrom: selectedTimeFrom,
      timeTo: selectedTimeTo,
      discount: appliedDiscount,
      amount: discountedAmount.toFixed(2),
    };

    const savedBooking = await saveBookingToBackend(bookingDetails);
    if (savedBooking) {
      setServiceDetails(bookingDetails);
      setStage(5);
    }
  };

  // Render different components based on the current stage
  return (
    <div className="app-container">
      <ProgressBar currentStage={stage} />

      {stage === 1 && <ServiceDetails onConfirmBooking={handleConfirmService} />}

      {stage === 2 && (
        <>
          <CalendarComponent onDateSelect={handleDateSelect} />
          <TimeSlots onTimeSelect={handleTimeSelect} />
          <button onClick={handleProceedToUserInfo} className="confirm-button">
            Proceed to User Info
          </button>
        </>
      )}

      {stage === 3 && (
        <>
          {discountMessage && <p>{discountMessage}</p>}
          <BookingForm onProceedToService={handleProceedToService} checkUserExists={checkUserExists} />
        </>
      )}

      {stage === 4 && (
        <BookingConfirmed
          bookingDetails={{
            ...userDetails,
            ...serviceDetails,
            bookingDate: selectedDate,
            timeFrom: selectedTimeFrom,
            timeTo: selectedTimeTo
          }}
          onConfirmBooking={handleConfirmBooking}
        />
      )}

      {stage === 5 && (
        <div>
          <h3>Your booking has been saved successfully!</h3>
          <BookingConfirmed bookingDetails={serviceDetails} />
        </div>
      )}
    </div>
  );
}

export default App;
