import React, { useState } from 'react';
import TimePicker from 'react-time-picker';
import '../styles.css';  // Import the global styles

function TimeSlots({ onTimeSelect }) {
  const [timeFrom, setTimeFrom] = useState('');
  const [timeTo, setTimeTo] = useState('');

  const handleTimeFromChange = (value) => {
    setTimeFrom(value);
    onTimeSelect(value, timeTo);
  };

  const handleTimeToChange = (value) => {
    setTimeTo(value);
    onTimeSelect(timeFrom, value);
  };

  return (
    <div>
      <h2>Select a Time Slot</h2>
      <label>From:</label>
      <TimePicker onChange={handleTimeFromChange} value={timeFrom} />
      <label>To:</label>
      <TimePicker onChange={handleTimeToChange} value={timeTo} />
    </div>
  );
}

export default TimeSlots;
