import React, { useState, useEffect } from 'react';
import '../ServiceDetails.css';

const priceRanges = [
  { min: 0, max: 49, priceAfterRut: 1499, priceBeforeRut: 2998 },
  { min: 50, max: 59, priceAfterRut: 1699, priceBeforeRut: 3398 },
  { min: 60, max: 69, priceAfterRut: 1899, priceBeforeRut: 3798 },
  { min: 70, max: 79, priceAfterRut: 2099, priceBeforeRut: 4198 },
  { min: 80, max: 89, priceAfterRut: 2299, priceBeforeRut: 4698 },
  { min: 90, max: 99, priceAfterRut: 2499, priceBeforeRut: 4998 },
  { min: 100, max: 109, priceAfterRut: 2699, priceBeforeRut: 5398 },
  { min: 110, max: 119, priceAfterRut: 2899, priceBeforeRut: 5798 },
  { min: 120, max: 129, priceAfterRut: 3099, priceBeforeRut: 6198 },
  { min: 130, max: 139, priceAfterRut: 3299, priceBeforeRut: 6598 },
  { min: 140, max: 149, priceAfterRut: 3499, priceBeforeRut: 6998 },
  { min: 150, max: 159, priceAfterRut: 3699, priceBeforeRut: 7398 },
  { min: 160, max: 169, priceAfterRut: 3899, priceBeforeRut: 7798 },
  { min: 170, max: 179, priceAfterRut: 4099, priceBeforeRut: 8198 },
  { min: 180, max: 189, priceAfterRut: 4299, priceBeforeRut: 8598 },
  { min: 190, max: 4499, priceAfterRut: 4499 + 15, priceBeforeRut: 4499 * 2 },
];

function ServiceDetails({ onProceedToUser, discount: initialDiscount }) {
  const [totalArea, setTotalArea] = useState(50); // Default value for slider
  const [priceAfterRut, setPriceAfterRut] = useState(0);
  const [priceBeforeRut, setPriceBeforeRut] = useState(0);

  useEffect(() => {
    // Calculate price based on total area
    const area = parseFloat(totalArea) || 0;
    const priceRange = priceRanges.find(
      (range) => area >= range.min && area <= range.max
    );

    if (priceRange) {
      setPriceAfterRut(priceRange.priceAfterRut);
      setPriceBeforeRut(priceRange.priceBeforeRut);
    }
  }, [totalArea]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const serviceDetails = {
      totalArea,
      priceAfterRut,
      priceBeforeRut,
    };
    onProceedToUser(serviceDetails);
  };

  return (
    <div className="service-details-container">
      <h2>Ange vilket service du vill!</h2>

      <form onSubmit={handleSubmit} className="service-form">
        <div className="form-group">
          <label>Total yta (kvm):</label>
          <div className="slider-container">
            <input
              type="range"
              min="0"
              max="4499"
              value={totalArea}
              step="1"
              onChange={(e) => setTotalArea(e.target.value)}
            />
            <div className="slider-value" style={{ left: `${(totalArea / 4499) * 100}%` }}>
              {totalArea} kvm
            </div>
          </div>
        </div>

        <div className="form-group">
          <label>Pris efter Rut: {priceAfterRut} kr</label>
        </div>

        <div className="form-group">
          <label>Pris innan Rut: {priceBeforeRut} kr</label>
        </div>

        <button type="submit" className="confirm-button-datum">Forts√§tt till datum och tid</button>
      </form>
    </div>
  );
}

export default ServiceDetails;
