import React from 'react';
import { useSelector } from 'react-redux'; // Import the selector

function Confirmation() {
  const bookingDetails = useSelector((state) => state.booking); // Get booking data from the Redux store

  const { selectedDate, selectedTimeFrom, selectedTimeTo, serviceDetails } = bookingDetails;

  const userName = "John Doe";
  const userEmail = "johndoe@example.com";
  const userPhone = "+1 234 567 890";

  return (
    <div className="booking-confirmed-container">
      <h2>Booking Confirmed</h2>
      <p>Thank you for booking with us, {userName}!</p>

      <div className="booking-info">
        <div>Name: {userName}</div>
        <div>Email: {userEmail}</div>
        <div>Phone: {userPhone}</div>
        <div>Date: {selectedDate && selectedDate.toDateString()}</div>
        <div>Time: {selectedTimeFrom} - {selectedTimeTo}</div>
        <div>Service: {serviceDetails?.serviceName}</div>
        <div>Total Amount: ${serviceDetails?.amount}</div>
      </div>
    </div>
  );
}

export default Confirmation;
