import React, { useState } from 'react';
import '../styles.css';  // Import the global styles

function ServiceDetails({ onServiceDetailsSubmit }) {
  const [serviceName, setServiceName] = useState('');  // New state for serviceName
  const [servicePrice, setServicePrice] = useState('');
  const [totalArea, setTotalArea] = useState('');
  const [discount, setDiscount] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Pass the service details to the parent component
    onServiceDetailsSubmit({
      serviceName,  // Add serviceName to the data being passed
      servicePrice: parseFloat(servicePrice),
      totalArea: parseFloat(totalArea),
      discount: parseFloat(discount),
      amount: parseFloat(amount)
    });
  };

  return (
    <div className="service-details-container">
      <h2>Enter Service Details</h2>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Service Name:</label>
          <input
            type="text"
            value={serviceName}
            onChange={(e) => setServiceName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Service Price:</label>
          <input
            type="number"
            step="0.01"
            value={servicePrice}
            onChange={(e) => setServicePrice(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Total Area (sqm):</label>
          <input
            type="number"
            step="0.01"
            value={totalArea}
            onChange={(e) => setTotalArea(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Discount (%):</label>
          <input
            type="number"
            step="0.01"
            value={discount}
            onChange={(e) => setDiscount(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Amount:</label>
          <input
            type="number"
            step="0.01"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>

        <button type="submit">Proceed to Confirm</button>
      </form>
    </div>
  );
}

export default ServiceDetails;
