import React, { useState } from 'react';
import '../styles.css';  // Import the global styles

function BookingForm({ onProceedToService }) {  // Change the prop name for clarity
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [postalCode, setPostalCode] = useState('');

  const handleProceedToService = (e) => {
    e.preventDefault();

    // Pass user details to the parent component
    const userDetails = {
      name,
      email,
      phone,
      address,
      postalCode,
    };
    onProceedToService(userDetails);  // Pass data up to App component
  };

  return (
    <div className="booking-form-container">
      <h2>Enter Your Details</h2>

      <form onSubmit={handleProceedToService}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Phone:</label>
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Address:</label>
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Postal Code:</label>
          <input
            type="text"
            value={postalCode}
            onChange={(e) => setPostalCode(e.target.value)}
            required
          />
        </div>

        <button type="submit">Proceed to Service Details</button>
      </form>
    </div>
  );
}

export default BookingForm;
