import React, { useState } from 'react';
import CalendarComponent from './components/Calendar';
import TimeSlots from './components/TimeSlots';
import BookingForm from './components/BookingForm';
import ServiceDetails from './components/ServiceDetails';
import BookingConfirmed from './components/BookingConfirmed';
import ProgressBar from './components/ProgressBar';
import './styles.css';

function App() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeFrom, setSelectedTimeFrom] = useState('');
  const [selectedTimeTo, setSelectedTimeTo] = useState('');
  const [userDetails, setUserDetails] = useState({});
  const [serviceDetails, setServiceDetails] = useState({});
  const [stage, setStage] = useState(1); // Start with ServiceDetails
  const [discountMessage, setDiscountMessage] = useState('');
  const [discount, setDiscount] = useState(0);

  // Step 1: Handle service details confirmation
  const handleConfirmService = (serviceDetails) => {
    setServiceDetails(serviceDetails);
    setStage(2); // Move to Calendar and TimeSlots
  };

  // Step 2: Handle date selection from Calendar
  const handleDateSelect = (date) => {
    setSelectedDate(date);
  };

  // Step 3: Handle time slot selection
  const handleTimeSelect = (from, to) => {
    setSelectedTimeFrom(from);
    setSelectedTimeTo(to);
  };

  // Step 4: Check if user exists (mock function)
  const checkUserExists = async (email) => {
    // Mock API call, always returns true for the demo
    return email === 'bara.abdullatif@outlook.com'; // Assuming this email already exists
  };

  // Step 5: User details form submission handler
  const handleProceedToService = async (details) => {
    const userExists = await checkUserExists(details.email);
    if (userExists) {
      setDiscountMessage('Congratulations! You are already in our system and get a 10% discount!');
      setDiscount(10); // Automatically set discount to 10% for returning users
    } else {
      setDiscountMessage('');
      setDiscount(0); // Default to 0% discount for new users
    }

    setUserDetails(details); // Store user details
    setStage(4); // Move to service details
  };

  // Step 6: Save booking details to backend (mock function)
  const saveBookingToBackend = async (bookingDetails) => {
    console.log('Saving booking:', bookingDetails);
    // Mock API call to save booking
  };

  // Step 7: Confirm booking and merge all details
  const handleConfirmBooking = async (serviceDetails) => {
    const discountedAmount = serviceDetails.amount - (serviceDetails.amount * (discount / 100)); // Apply discount

    const bookingDetails = {
      ...userDetails, // Combine user details
      ...serviceDetails, // Combine service details
      bookingDate: selectedDate.toISOString(), // Format selected date to ISO string
      timeFrom: selectedTimeFrom, // Include selected time from
      timeTo: selectedTimeTo, // Include selected time to
      discount, // Apply discount
      amount: discountedAmount.toFixed(2), // Final amount with discount
    };

    await saveBookingToBackend(bookingDetails); // Save booking to backend
    setServiceDetails(bookingDetails); // Update state with complete booking details
    setStage(5); // Move to confirmation stage
  };

  return (
    <div>
      <ProgressBar currentStage={stage} />

      {stage === 1 && <ServiceDetails discount={discount} onConfirmBooking={handleConfirmService} />}
      {stage === 2 && (
        <>
          <CalendarComponent onDateSelect={handleDateSelect} />
          <TimeSlots onTimeSelect={handleTimeSelect} />
          <button onClick={() => setStage(3)} className="confirm-button">Proceed</button>
        </>
      )}
      {stage === 3 && (
        <>
          {discountMessage && <p>{discountMessage}</p>}
          <BookingForm onProceedToService={handleProceedToService} />
        </>
      )}
      {stage === 4 && <ServiceDetails discount={discount} onConfirmBooking={handleConfirmBooking} />}
      {stage === 5 && <BookingConfirmed bookingDetails={serviceDetails} />}
    </div>
  );
}

export default App;
