import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { setServiceDetails } from '../store'; // Import the action

function ServiceDetails({ setStage }) {
  const [selectedService, setSelectedService] = useState('Clean house');
  const [servicePrice, setServicePrice] = useState(100);
  const [totalArea, setTotalArea] = useState(50);
  const [discount, setDiscount] = useState(0);
  const [amount, setAmount] = useState(0);
  const dispatch = useDispatch();

  useEffect(() => {
    const priceBeforeDiscount = totalArea * servicePrice;
    const discountValue = priceBeforeDiscount * (discount / 100);
    const finalAmount = priceBeforeDiscount - discountValue;
    setAmount(finalAmount.toFixed(2));
  }, [totalArea, servicePrice, discount]);

  const handleConfirmService = () => {
    dispatch(
      setServiceDetails({
        serviceName: selectedService,
        servicePrice,
        totalArea,
        discount,
        amount,
      })
    );
    setStage(5); // Move to confirmation stage
  };

  return (
    <div className="service-details-container">
      <h2>Enter Service Details</h2>
      {/* Service form content */}
      <button onClick={handleConfirmService}>Confirm Booking</button>
    </div>
  );
}

export default ServiceDetails;
