import React, { useState } from 'react';
import Calendar from 'react-calendar';
import { useDispatch } from 'react-redux';
import { setSelectedDate } from '../store'; // Import the action

function CalendarComponent({ setStage }) {
  const [selectedDate, setSelectedDateLocal] = useState(new Date());
  const dispatch = useDispatch();

  const handleDateChange = (date) => {
    setSelectedDateLocal(date);
    dispatch(setSelectedDate(date)); // Dispatch the action to store the selected date
    setStage(2); // Move to the next stage
  };

  return (
    <div className="calendar-container">
      <h2>Select a Date</h2>
      <Calendar
        onChange={handleDateChange}
        value={selectedDate}
        minDate={new Date()} // Disable selection of past dates
      />
    </div>
  );
}

export default CalendarComponent;
